            </div>
            <div class="col-lg-6">
                <div class="input-group mb-4">
                                            <span class="input-group-text bg-gray-400 pe-0">
                                                <svg width="21" height="20" viewBox="0 0 21 20" fill="none"
                                                     xmlns="http://www.w3.org/2000/svg">
                                                    <path
                                                        d="M4.28516 12H12.2852V10H4.28516V12ZM4.28516 9H16.2852V7H4.28516V9ZM4.28516 6H16.2852V4H4.28516V6ZM0.285156 20V2C0.285156 1.45 0.48099 0.979167 0.872656 0.5875C1.26432 0.195833 1.73516 0 2.28516 0H18.2852C18.8352 0 19.306 0.195833 19.6977 0.5875C20.0893 0.979167 20.2852 1.45 20.2852 2V14C20.2852 14.55 20.0893 15.0208 19.6977 15.4125C19.306 15.8042 18.8352 16 18.2852 16H4.28516L0.285156 20ZM3.43516 14H18.2852V2H2.28516V15.125L3.43516 14Z"
                                                        fill="#000C40"
                                                    />
                                                </svg>
                                            </span>
                    <input type="text" name="message" class="form-control border-start-0 fw-light"
                           placeholder="Message"/>
                </div>
            </div>
            <div class="col-md-6">
                <div class="g-recaptcha" data-sitekey="{{ $recaptchaKey }}"></div>
                <script src="https://www.google.com/recaptcha/api.js" async defer></script>
            </div>
            <div class="col-md-6">
                <button type="submit" class="btn btn-teal text-primary w-100 h-100 fs-18">Submit</button>
            </div>
        </form>
    </div>
</section>

<script src="https://cdn.jsdelivr.net/npm/sweetalert2@11"></script>
<script>
    document.getElementById('contactForms').addEventListener('submit', function (event) {
        event.preventDefault();

        /*
        var response = grecaptcha.getResponse();

        if (response.length === 0) {
            Swal.fire({
                title: 'Uyarı!',
                text: 'Lütfen robot olmadığınızı doğrulayın.',
                icon: 'warning',
                confirmButtonText: 'Tamam'
            });
            return false;
        } */

        // Form verilerini topla
        const formData = new FormData(this);

        // AJAX isteği gönder
        fetch('{{ route("form-submit") }}', {
            method: 'POST',
            body: formData,
            headers: {
                'Accept': 'application/json',
                'X-Requested-With': 'XMLHttpRequest',
            },
            credentials: 'same-origin'
        })
            .then(response => {
                if (!response.ok) {
                    return response.json().then(json => Promise.reject(json));
                }
                return response.json();
            })
            .then(data => {
                if (data.status === 'success') {
                    Swal.fire({
                        title: 'Başarılı!',
                        text: data.message,
                        icon: 'success',
                        confirmButtonText: 'Tamam'
                    }).then((result) => {
                        if (result.isConfirmed) {
                            // Formu sıfırla
                            this.reset();
                            grecaptcha.reset();
                        }
                    });
                } else {
                    throw new Error(data.message || 'Bir hata oluştu');
                }
            })
            .catch(error => {
                console.error('Error:', error);
                Swal.fire({
                    title: 'Hata!',
                    text: error.message || 'Bir hata oluştu',
                    icon: 'error',
                    confirmButtonText: 'Tamam'
                });
            });
    });
</script>
